INSERT INTO `pets` VALUES (1, 'dog', 'Anna\'s Angels Dog Rescue', 'Faith (Daisy Roo)', 'Aulac, NB', 'German Shepherd Dog Mix', 'Young', 'female', 'Large', 'Brown / Chocolate', 'Daisy Roo is a fun loving dog who loves adventure! She gets along well with other dogs, cats and she does well with children. Daisy has spent some time in doggy daycare and enjoys socialization. She needs a family who is active and enjoys doing activities such as hiking, swimming, etc. She would benefit from a life that enables her to go to dog parks and socialize or just simply run freely with stimulation both physically and mentally. \n\nShes a huge snuggle bug and very loving. She loves her humans and fur friends alike! Whether its one person or a whole family who adopts her, she will be happy. Can you give this gem a life of fun and excitement?', '68911209', 'aadrapps@gmail.com', '', 'https://dl5zpyw5k3jeb.cloudfront.net/photos/pets/68911209/1/?bust=1697395921&width=720,https://dl5zpyw5k3jeb.cloudfront.net/photos/pets/68911209/2/?bust=1697395907&width=720,https://dl5zpyw5k3jeb.cloudfront.net/photos/pets/68911209/3/?bust=1697395925&width=720,https://dl5zpyw5k3jeb.cloudfront.net/photos/pets/68911209/4/?bust=1697395922&width=720,https://dl5zpyw5k3jeb.cloudfront.net/photos/pets/68911209/5/?bust=1697395924&width=720,https://dl5zpyw5k3jeb.cloudfront.net/photos/pets/68911209/6/?bust=1697395923&width=720,https://dl5zpyw5k3jeb.cloudfront.net/photos/pets/68911209/1/?bust=1697395921&width=720,https://dl5zpyw5k3jeb.cloudfront.net/photos/pets/68911209/2/?bust=1697395907&width=720,https://dl5zpyw5k3jeb.cloudfront.net/photos/pets/68911209/3/?bust=1697395925&width=720,https://dl5zpyw5k3jeb.cloudfront.net/photos/pets/68911209/4/?bust=1697395922&width=720,https://dl5zpyw5k3jeb.cloudfront.net/photos/pets/68911209/5/?bust=1697395924&width=720,https://dl5zpyw5k3jeb.cloudfront.net/photos/pets/68911209/6/?bust=1697395923&width=720', '2023-10-19 01:16:45', NULL, NULL);
